import React, { useState, useEffect } from 'react';
import axios from 'axios';
import CircleIcon from '@mui/icons-material/Circle'; // Import Material UI icons
import '../styles/StatusTable.css';

function StatusTable() {
    const [tableData, setTableData] = useState([]);
    const [latestReport, setLatestReport] = useState({});
    const [loading, setLoading] = useState(true);

    // Fetch status data from the updated API endpoint
    useEffect(() => {
        axios.get('http://127.0.0.1:5000/api/status')
            .then(response => {
                setTableData(response.data.table); // Updated to match the API response structure
                setLatestReport(response.data.latest_report); // Fetch the latest report info
                setLoading(false);
            })
            .catch(error => {
                console.error("Error fetching status data:", error);
                setLoading(false);
            });
    }, []);

    const getStatusIcon = (status) => {
        const iconStyle = { width: "12px", height: "12px", margin: "0", padding: "0" };

        if (status === "operational") return <CircleIcon style={{ ...iconStyle, color: "green" }} />;
        if (status === "maintanance") return <CircleIcon style={{...iconStyle, color: "yellow",}}/>;
        if (status === "offline") return <CircleIcon style={{ ...iconStyle, color: "red" }} />;
        return <CircleIcon style={{ ...iconStyle, color: "gray" }} />;
    };


    if (loading) {
        return <p>Loading data...</p>;
    }

    return (
        <div className="container">
            <h2>Status Table</h2>
            <table>
                <thead>
                    <tr>
                        <th>Telescope</th>
                        <th>Mount</th>
                        <th>Focuser</th>
                        <th>Filterwheel</th>
                        <th>Camera</th>
                    </tr>
                </thead>
                <tbody>
                    {tableData.map((row, index) => (
                        <tr key={index}>
                            <td>{row.Telescope}</td>
                            <td>{getStatusIcon(row.Mount)}</td>
                            <td>{getStatusIcon(row.Focuser)}</td>
                            <td>{getStatusIcon(row.Filterwheel)}</td>
                            <td>{getStatusIcon(row.Camera)}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <p className="footer">
                Reported by {latestReport.reported_by} on {latestReport.timestamp}
            </p>
        </div>
    );
}

export default StatusTable;
