import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/TargetForm.css';
import { Autocomplete, TextField, Button, RadioGroup, FormControlLabel, Radio, MenuItem, Select, Table, TableBody, TableCell, TableHead, TableRow } from '@mui/material';

function TargetForm() {
    const [targets, setTargets] = useState([]);
    const [target, setTarget] = useState('');
    const [ra, setRa] = useState('');
    const [dec, setDec] = useState('');
    const [exposure, setExposure] = useState('');
    const [mode, setMode] = useState('Spec'); // Default value is 'Spec'
    const [specFileOptions, setSpecFileOptions] = useState(['specall.specmode']); // State for file options
    const [selectedSpecFile, setSelectedSpecFile] = useState('specall.specmode'); // Selected file
    const [specFileData, setSpecFileData] = useState({}); // Data from the specmode file
    const [message, setMessage] = useState('');
    const [isCustomTarget, setIsCustomTarget] = useState(true); // Allow RA/DEC editing by default

    const wavelengthRange = Array.from({ length: 61 }, (_, i) => 3000 + i * 100); // 3000Å to 9000Å in 100Å steps
    const filters = ['u', 'r', 'g', 'i', 'z']; // Updated filters

    useEffect(() => {
        // Fetch targets from the backend
        axios.get('http://127.0.0.1:5000/api/targets')
            .then(response => {
                setTargets(response.data);
            })
            .catch(error => {
                console.error('Error fetching targets:', error);
            });
    }, []);

    useEffect(() => {
        if (mode === 'Spec') {
            // Fetch file options from the backend
            axios.get('http://127.0.0.1:5000/api/spec-options')
                .then(response => {
                    setSpecFileOptions(response.data); // Update state with fetched filenames
                })
                .catch(error => {
                    console.error('Error fetching spec file options:', error);
                });
        }
    }, [mode]); // Fetch options when mode changes to "Spec"

    useEffect(() => {
        if (selectedSpecFile) {
            // Fetch the content of the selected specmode file
            axios.get(`http://127.0.0.1:5000/api/spec-file?file=${selectedSpecFile}`)
                .then(response => {
                    setSpecFileData(response.data); // Update state with file data
                })
                .catch(error => {
                    console.error('Error fetching spec file data:', error);
                });
        }
    }, [selectedSpecFile]);

    const handleInputChange = (event, value) => {
        setTarget(value);

        // Check if the input matches a predefined target
        const selectedTarget = targets.find((t) => t.name === value);
        if (selectedTarget) {
            // If a predefined target is selected
            setRa(selectedTarget.ra);
            setDec(selectedTarget.dec);
            setIsCustomTarget(false); // Disable RA/DEC editing
        } else {
            // If it's a custom target
            setRa('');
            setDec('');
            setIsCustomTarget(true); // Enable RA/DEC editing
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://127.0.0.1:5000/api/send_email', {
                target,
                ra,
                dec,
                exposure,
                mode,
            });
            setMessage(response.data.message);
        } catch (error) {
            console.error('Error sending email:', error);
            setMessage('Failed to submit form.');
        }
    };

    const isWavelengthInRange = (mValue, wavelength) => {
        const match = mValue.match(/^m(\d+)/);
        if (!match) return false;
        const center = parseInt(match[1], 10) * 10; // Convert m450 to 4500Å
        const rangeStart = center - 125;
        const rangeEnd = center + 125;
        return wavelength >= rangeStart && wavelength <= rangeEnd;
    };

    const isFilterPresent = (filter) => {
        return Object.values(specFileData).flat().includes(filter); // Check if filter exists in the spec file
    };

    return (
        <div>
            <h2>7DT Target of Opportunity (ToO) Request</h2>
            <form onSubmit={handleSubmit} className="form">
                <div className="group-container">
                    <label className="default-label">Target:</label>
                    <Autocomplete
                        options={targets}
                        getOptionLabel={(option) => option.name || ''}
                        inputValue={target}
                        onInputChange={handleInputChange} // Triggered as user types
                        renderInput={(params) => (
                            <TextField
                                {...params}
                                label="Target"
                                variant="outlined"
                                fullWidth
                            />
                        )}
                        freeSolo // Allows custom input
                        openOnFocus // Opens dropdown when focused
                        className="input-field"
                    />
                </div>
                <div className="group-container">
                    <label className="default-label">Spec File:</label>
                    <Select
                        value={selectedSpecFile}
                        onChange={(e) => setSelectedSpecFile(e.target.value)}
                        fullWidth
                    >
                        {specFileOptions.map((file) => (
                            <MenuItem key={file} value={file}>
                                {file}
                            </MenuItem>
                        ))}
                    </Select>
                </div>
                {mode === 'Spec' && selectedSpecFile && (
                    <div className="table-container">
                        <Table>
                            <TableHead>
                                <TableRow>
                                    <TableCell>Wavelength (Å)</TableCell>
                                    {filters.map((filter) => (
                                        <TableCell key={filter}>{filter}</TableCell>
                                    ))}
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {wavelengthRange.map((wavelength) => (
                                    <TableRow key={wavelength}>
                                        <TableCell>{wavelength}</TableCell>
                                        {filters.map((filter) => (
                                            <TableCell
                                                key={filter}
                                                style={{
                                                    backgroundColor: isFilterPresent(filter)
                                                        ? 'green'
                                                        : 'transparent',
                                                }}
                                            />
                                        ))}
                                    </TableRow>
                                ))}
                                {Object.values(specFileData)
                                    .flat()
                                    .filter((value) => value.startsWith('m'))
                                    .map((mValue) => (
                                        <TableRow key={mValue}>
                                            <TableCell
                                                colSpan={filters.length + 1}
                                                style={{
                                                    backgroundColor: 'green',
                                                    textAlign: 'center',
                                                }}
                                            >
                                                {mValue}
                                            </TableCell>
                                        </TableRow>
                                    ))}
                            </TableBody>
                        </Table>
                    </div>
                )}
                <Button
                    type="submit"
                    variant="contained"
                    color="primary"
                    fullWidth
                    className="submit-button"
                >
                    Submit
                </Button>
            </form>
            {message && <p className="message">{message}</p>}
        </div>
    );
}

export default TargetForm;
