import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import '../styles/TargetForm.css';
import {
    Autocomplete,
    TextField,
    Button,
    RadioGroup,
    FormControlLabel,
    Radio,
    MenuItem,
    Select,
    Checkbox
} from '@mui/material';

import {
    Chart as ChartJS,
    LinearScale,
    Title,
    Tooltip,
    ScatterController,
} from 'chart.js';

ChartJS.register(LinearScale, Title, Tooltip, ScatterController);

function TargetForm() {
    const [targets, setTargets] = useState([]);
    const [target, setTarget] = useState('');
    const [ra, setRa] = useState('');
    const [dec, setDec] = useState('');
    const [exposure, setExposure] = useState('');
    const [mode, setMode] = useState('Spec'); // Default mode: 'Spec'
    const [specFileOptions, setSpecFileOptions] = useState(['specall.specmode']);
    const [selectedSpecFile, setSelectedSpecFile] = useState('specall.specmode');
    const [message, setMessage] = useState('');
    const [isCustomTarget, setIsCustomTarget] = useState(true);
    const [specFileData, setSpecFileData] = useState({});
    const [wavelengths, setWavelengths] = useState([]);
    const [filters, setFilters] = useState([]);
    const chartRef = useRef(null);

    const [selectedFilters, setSelectedFilters] = useState(['g']); // For checkboxes
    const [selectedNumber, setSelectedNumber] = useState(10); // For dropdown


    useEffect(() => {
        axios
            .get('http://127.0.0.1:5000/api/targets')
            .then((response) => setTargets(response.data))
            .catch((error) => console.error('Error fetching targets:', error));
    }, []);

    useEffect(() => {
        if (mode === 'Spec') {
            axios
                .get('http://127.0.0.1:5000/api/spec-options')
                .then((response) => setSpecFileOptions(response.data))
                .catch((error) => console.error('Error fetching spec file options:', error));
        }
    }, [mode]);

    useEffect(() => {
        if (selectedSpecFile) {
            axios
                .get(`http://127.0.0.1:5000/api/spec-file?file=${selectedSpecFile}`)
                .then((response) => {
                    setWavelengths(response.data.wavelengths);
                    setFilters(response.data.filters);
                })
                .catch((error) => console.error('Error fetching spec file data:', error));
        }
    }, [selectedSpecFile]);

    useEffect(() => {
        if (!chartRef.current) return;

        const ctx = chartRef.current.getContext('2d');
        let wavelengthChart = ChartJS.getChart(ctx);

        if (!wavelengthChart) {
            wavelengthChart = new ChartJS(ctx, {
                type: 'scatter',
                data: { labels: [''], datasets: [] },
                options: {
                    responsive: true,
                    maintainAspectRatio: false,
                    scales: {
                        x: { type: 'linear', min: 3500, max: 9500, title: { display: false } },
                        y: { display: false },
                    },
                    plugins: { legend: { display: false } },
                },
                plugins: [
                    {
                        id: 'customSpans',
                        beforeDraw: (chart) => {
                            const ctx = chart.ctx;
                            const xScale = chart.scales.x;
                            wavelengths.forEach((wav) => {
                                const xMin = xScale.getPixelForValue((wav - 12.5) * 10);
                                const xMax = xScale.getPixelForValue((wav + 12.5) * 10);
                                ctx.fillStyle = 'green';
                                ctx.fillRect(
                                    xMin,
                                    chart.chartArea.top,
                                    xMax - xMin,
                                    chart.chartArea.bottom - chart.chartArea.top
                                );
                            });
                        },
                    },
                ],
            });
        } else {
            wavelengthChart.data.datasets = [];
            wavelengthChart.update();
        }

        return () => wavelengthChart?.destroy();
    }, [wavelengths, mode]);

    const handleInputChange = (event, value) => {
        setTarget(value);
        const selectedTarget = targets.find((t) => t.name === value);
        if (selectedTarget) {
            setRa(selectedTarget.ra);
            setDec(selectedTarget.dec);
            setIsCustomTarget(false);
        } else {
            setRa('');
            setDec('');
            setIsCustomTarget(true);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://127.0.0.1:5000/api/send_email', {
                target,
                ra,
                dec,
                exposure,
                mode,
            });
            setMessage(response.data.message);
        } catch (error) {
            console.error('Error sending email:', error);
            setMessage('Failed to submit form.');
        }
    };

    const handleCheckboxChange = (filter) => {
        setSelectedFilters((prev) =>
            prev.includes(filter)
                ? prev.filter((item) => item !== filter) // Remove filter if unchecked
                : [...prev, filter] // Add filter if checked
        );
    };

    return (
        <div className="container">
            <form onSubmit={handleSubmit} className="form">
                <div className="group-container">
                    <label className="default-label">Target:</label>
                    <Autocomplete
                        options={targets}
                        getOptionLabel={(option) => option.name || ''}
                        inputValue={target}
                        onInputChange={handleInputChange}
                        renderInput={(params) => (
                            <TextField {...params} label="Target" variant="outlined" fullWidth size="small" />
                        )}
                        freeSolo
                        className="input-field"
                    />
                </div>
                <div className="group-container">
                    <label className="default-label">R.A.:</label>
                    <TextField
                        label="hh:mm:ss or degrees"
                        variant="outlined"
                        value={ra}
                        onChange={(e) => setRa(e.target.value)}
                        fullWidth
                        disabled={!isCustomTarget} 
                        className="input-field"
                        size="small"
                    />
                </div>
                <div className="group-container">
                    <label className="default-label">Dec.:</label>
                    <TextField
                        label="dd:mm:ss or degrees"
                        variant="outlined"
                        value={dec}
                        onChange={(e) => setDec(e.target.value)}
                        fullWidth
                        disabled={!isCustomTarget} 
                        className="input-field"
                        size="small"
                    />
                </div>
                <div className="group-container">
                    <label className="default-label">Exposure:</label>
                    <TextField
                        label="seconds"
                        variant="outlined"
                        value={exposure}
                        onChange={(e) => setExposure(e.target.value)}
                        fullWidth
                        className="input-field"
                        size="small"
                    />
                </div>
                <div className="group-container">
                    <label className="default-label">Mode:</label>
                    <RadioGroup
                        row
                        value={mode}
                        onChange={(e) => setMode(e.target.value)}
                        className="radio-group"
                    >   
                        <FormControlLabel
                            value="Spec"
                            control={<Radio />}
                            label="Spec"
                        />
                        <FormControlLabel
                            value="Deep"
                            control={<Radio />}
                            label="Deep"
                        />
                    </RadioGroup>
                </div>
                {mode === 'Spec' && (
                    <div className="mode-options">
                        <div className="group-container">
                            <label className="smaller-label">Spec File:</label>
                            <Select
                                value={selectedSpecFile}
                                onChange={(e) => setSelectedSpecFile(e.target.value)}
                                fullWidth
                                size="small"
                            >
                                {specFileOptions.map((file) => (
                                    <MenuItem key={file} value={file}>
                                        {file}
                                    </MenuItem>
                                ))}
                            </Select>
                        </div>
                        <label className="smaller-label">Wavelength (Å): </label>
                        <div className="group-container"  style={{marginTop: '5px'}}>
                            <canvas ref={chartRef} style={{height: '50px'}} />
                        </div>
                        <label className="smaller-label">Filter: </label>
                        <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                            <tbody>
                                <tr>
                                    {['u', 'g', 'r', 'i', 'z'].map((filter) => (
                                        <td
                                            key={filter}
                                            style={{
                                                border: '1px solid #ddd',
                                                padding: '1px',
                                                width: '20%',
                                                backgroundColor: filters.includes(filter) ? 'green' : 'white',
                                                color: filters.includes(filter) ? 'white' : 'gray',
                                                textAlign: 'center',
                                            }}
                                        >
                                            {filter}
                                        </td>
                                    ))}
                                </tr>
                            </tbody>
                        </table>
                    </div>
                )}
                {mode === 'Deep' && (
                    <div className="mode-options">
                        <div className="checkbox-group">
                        <label className="smaller-label">Filters:</label>
                            <FormControlLabel
                                control={<Checkbox />}
                                label="g"
                                checked={selectedFilters.includes('g')}
                                onChange={() => handleCheckboxChange('g')}
                            />
                            <FormControlLabel
                                control={<Checkbox />}
                                label="r"
                                checked={selectedFilters.includes('r')}
                                onChange={() => handleCheckboxChange('r')}
                            />
                            <FormControlLabel
                                control={<Checkbox />}
                                label="i"
                                checked={selectedFilters.includes('i')}
                                onChange={() => handleCheckboxChange('i')}
                            />
                        </div>
                        <div className="dropdown-container">
                            <label className="smaller-label"># of telescopes</label>
                            <Select
                                value={selectedNumber}
                                onChange={(e) => setSelectedNumber(e.target.value)}
                                fullWidth
                                sx={{ "& .MuiInputBase-input": { fontSize: 10, height: 3, padding: 0.5 } }}
                            >
                                {Array.from({ length: 20 }, (_, i) => i + 1).map((num) => (
                                    <MenuItem key={num} value={num}>
                                        {num}
                                    </MenuItem>
                                ))}
                            </Select>
                        </div>
                    </div>
                )}
                <Button type="submit" variant="contained" color="primary" className="submit-button">
                    Submit
                </Button>
            </form>
            {message && <p className="message">{message}</p>}
        </div>
    );
}

export default TargetForm;
