import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/DailyScheduleTable.css';

function DailyScheduleTable() {
    const [scheduleData, setScheduleData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [isCollapsed, setIsCollapsed] = useState(false); // State to manage collapse

    // Fetch daily schedule data
    useEffect(() => {
        axios.get('http://127.0.0.1:5000/api/daily_schedule')
            .then(response => {
                setScheduleData(response.data);
                setLoading(false);
            })
            .catch(error => {
                console.error("Error fetching daily schedule:", error);
                setLoading(false);
            });
    }, []);

    const toggleCollapse = () => {
        setIsCollapsed(!isCollapsed);
    };

    if (loading) {
        return <p>Loading data...</p>;
    }

    return (
        <div className="container">
            <h2>Daily Observing Schedule</h2>
            <div className="collapsible-container">
                <button className="collapse-button" onClick={toggleCollapse}>
                    {isCollapsed ? "Show Daily Schedule" : "Hide Daily Schedule"}
                </button>
                {!isCollapsed && (
                    <div className="collapsible-content">
                        <table>
                            <thead>
                                <tr>
                                    <th>Object Name</th>
                                    <th>RA</th>
                                    <th>Dec</th>
                                    <th>Exposure Time</th>
                                    <th>Filter</th>
                                    <th>Status</th>
                                    <th>Observation Start</th>
                                    <th>Observation End</th>
                                </tr>
                            </thead>
                            <tbody>
                                {scheduleData.map((row, index) => (
                                    <tr key={index}>
                                        <td>{row.objname}</td>
                                        <td>{row.RA}</td>
                                        <td>{row.De}</td>
                                        <td>{row.exptime}</td>
                                        <td>{row.filter_ || 'None'}</td>
                                        <td>{row.status}</td>
                                        <td>{row.obs_starttime}</td>
                                        <td>{row.obs_endtime}</td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                )}
            </div>
        </div>
    );
}

export default DailyScheduleTable;
