import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/TargetForm.css';

function TargetForm() {
    const [targets, setTargets] = useState([]);
    const [selectedTarget, setSelectedTarget] = useState('');
    const [ra, setRa] = useState('');
    const [dec, setDec] = useState('');
    const [exposure, setExposure] = useState('');
    const [mode, setMode] = useState('');

    useEffect(() => {
        axios.get('http://127.0.0.1:5000/api/targets')
            .then(response => {
                setTargets(response.data);
            })
            .catch(error => {
                console.error('Error fetching targets:', error);
            });
    }, []);

    const handleTargetChange = (e) => {
        const targetName = e.target.value;
        setSelectedTarget(targetName);

        if (targetName === 'new target') {
            setRa('');
            setDec('');
        } else {
            const target = targets.find(t => t.name === targetName);
            if (target) {
                setRa(target.ra);
                setDec(target.dec);
            }
        }
    };

    return (
        <div className="container">
            <h2>7DT Target of Opportunity (ToO) Request</h2>
            <form>
                <div>
                    <label htmlFor="target">Target:</label>
                    <select id="target" value={selectedTarget} onChange={handleTargetChange}>
                        <option value="">--Select a Target--</option>
                        <option value="new target">New Target</option>
                        {targets.map((target, index) => (
                            <option key={index} value={target.name}>
                                {target.name}
                            </option>
                        ))}
                    </select>
                </div>

                <div>
                    <label htmlFor="ra">R.A. (degrees):</label>
                    <input
                        id="ra"
                        type="text"
                        value={ra}
                        onChange={(e) => setRa(e.target.value)}
                        disabled={selectedTarget !== 'new target'}
                        className={selectedTarget !== 'new target' ? 'disabled' : ''}
                    />
                </div>

                <div>
                    <label htmlFor="dec">Dec. (degrees):</label>
                    <input
                        id="dec"
                        type="text"
                        value={dec}
                        onChange={(e) => setDec(e.target.value)}
                        disabled={selectedTarget !== 'new target'}
                        className={selectedTarget !== 'new target' ? 'disabled' : ''}
                    />
                </div>

                <div>
                    <label htmlFor="exposure">Exposure (seconds):</label>
                    <input
                        id="exposure"
                        type="text"
                        value={exposure}
                        onChange={(e) => setExposure(e.target.value)}
                    />
                </div>

                <div>
                    <label htmlFor="mode">Mode:</label>
                    <select
                        id="mode"
                        value={mode}
                        onChange={(e) => setMode(e.target.value)}
                    >
                        <option value="">--Select a Mode--</option>
                        <option value="Mode A">Mode A</option>
                        <option value="Mode B">Mode B</option>
                        <option value="Mode C">Mode C</option>
                    </select>
                </div>

                <button type="submit">Submit</button>
            </form>
        </div>
    );
}

export default TargetForm;
