import React, { useState, useEffect } from 'react';
import { TextField, RadioGroup, FormControlLabel, Radio } from '@mui/material';
import '../styles/ColOptions.css';

function DetailedSettings({ detailedSettings, setDetailedSettings }) {
    const defaultValues = {
        singleFrameExposure: 60,
        imageCount: 1,
        priority: 50,
        gain: 2750,
        binning: 1,
        obsStartTime: '',
    };

    const {
        singleFrameExposure,
        imageCount,
        priority,
        gain,
        binning,
        obsStartTime,
    } = detailedSettings;

    const handleInputChange = (field, value) => {
        setDetailedSettings((prev) => ({
            ...prev,
            [field]: value,
        }));
    };

    useEffect(() => {
        // Automatically calculate image count based on exposure time
        const calculatedImageCount = Math.max(1, Math.floor(singleFrameExposure / 60));
        if (imageCount !== calculatedImageCount) {
            handleInputChange('imageCount', calculatedImageCount);
        }
    }, [singleFrameExposure]);

    return (
        <div className="mode-options">
            <div className="group-container">
                <label className="detail-label">Single Frame Exposure Time (seconds):</label>
                <TextField
                    value={singleFrameExposure}
                    onChange={(e) => handleInputChange('singleFrameExposure', parseInt(e.target.value) || 0)}
                    type="number"
                    variant="outlined"
                    size="small"
                />
            </div>

            <div className="group-container">
                <label className="detail-label"># of Images (Counts):</label>
                <TextField
                    value={imageCount}
                    onChange={(e) => handleInputChange('imageCount', parseInt(e.target.value) || 0)}
                    type="number"
                    variant="outlined"
                    size="small"
                />
            </div>

            <div className="group-container">
                <label className="detail-label">Priority:</label>
                <TextField
                    value={priority}
                    onChange={(e) => handleInputChange('priority', parseInt(e.target.value) || 0)}
                    type="number"
                    variant="outlined"
                    size="small"
                />
            </div>

            <div className="group-container">
                <label className="detail-label">Gain:</label>
                <TextField
                    value={gain}
                    onChange={(e) => handleInputChange('gain', parseInt(e.target.value) || 0)}
                    type="number"
                    variant="outlined"
                    size="small"
                />
            </div>

            <div className="group-container">
                <label className="detail-label">Binning:</label>
                <RadioGroup
                    row
                    value={binning}
                    onChange={(e) => handleInputChange('binning', parseInt(e.target.value))}
                >
                    <FormControlLabel value={1} control={<Radio />} label="1" />
                    <FormControlLabel value={2} control={<Radio />} label="2" />
                </RadioGroup>
            </div>

            <div className="group-container">
                <label className="detail-label">Observation Start Time:</label>
                <TextField
                    value={obsStartTime}
                    onChange={(e) => handleInputChange('obsStartTime', e.target.value)}
                    type="datetime-local"
                    variant="outlined"
                    size="small"
                />
            </div>
        </div>
    );
}

export default DetailedSettings;
