import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/StatusTable.css';

function StatusTable() {
    const [tableData, setTableData] = useState([]);
    const [latestReport, setLatestReport] = useState({});
    const [loading, setLoading] = useState(true);

    // Fetch status data
    useEffect(() => {
        axios.get('http://127.0.0.1:5000/api/status')
            .then(response => {
                setTableData(response.data.table);
                setLatestReport(response.data.latest_report);
                setLoading(false);
            })
            .catch(error => {
                console.error("Error fetching status data:", error);
                setLoading(false);
            });
    }, []);

    // Map status to CSS class
    const getStatusClass = (status) => {
        if (status === "operational") return "status-green";
        if (status === "maintanance") return "status-yellow";
        if (status === "offline") return "status-red";
        return "status-gray";
    };

    if (loading) {
        return <p>Loading data...</p>;
    }

    return (
        <div className="container">
            <h2>Status Table</h2>
            <table>
                <thead>
                    <tr>
                        <th>Telescope</th>
                        <th>Mount</th>
                        <th>Focuser</th>
                        <th>Filterwheel</th>
                        <th>Camera</th>
                    </tr>
                </thead>
                <tbody>
                    {tableData.map((row, index) => (
                        <tr key={index}>
                            <td>{row.Telescope}</td>
                            <td>
                                <span className={`status-ball ${getStatusClass(row.Mount)}`}></span>
                            </td>
                            <td>
                                <span className={`status-ball ${getStatusClass(row.Focuser)}`}></span>
                            </td>
                            <td>
                                <span className={`status-ball ${getStatusClass(row.Filterwheel)}`}></span>
                            </td>
                            <td>
                                <span className={`status-ball ${getStatusClass(row.Camera)}`}></span>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <p className="footer">
                Reported by {latestReport.reported_by} on {latestReport.timestamp}
            </p>
        </div>
    );
}

export default StatusTable;
