import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import '../styles/TargetForm.css';
import {
    Autocomplete,
    TextField,
    Button,
    RadioGroup,
    FormControlLabel,
    Radio,
    MenuItem,
    Select,
} from '@mui/material';

import {
    Chart as ChartJS,
    LinearScale,
    Title,
    Tooltip,
    ScatterController,
} from 'chart.js';

ChartJS.register(LinearScale, Title, Tooltip, ScatterController);

function TargetForm() {
    const [targets, setTargets] = useState([]);
    const [target, setTarget] = useState('');
    const [ra, setRa] = useState('');
    const [dec, setDec] = useState('');
    const [exposure, setExposure] = useState('');
    const [mode, setMode] = useState('Spec');
    const [specFileOptions, setSpecFileOptions] = useState(['specall.specmode']);
    const [selectedSpecFile, setSelectedSpecFile] = useState('specall.specmode');
    const [message, setMessage] = useState('');
    const [isCustomTarget, setIsCustomTarget] = useState(true);
    const [wavelengths, setWavelengths] = useState([]);
    const [filters, setFilters] = useState([]);
    const chartRef = useRef(null);

    useEffect(() => {
        const fetchTargets = async () => {
            try {
                const response = await axios.get('http://127.0.0.1:5000/api/targets');
                setTargets(response.data);
            } catch (error) {
                console.error('Error fetching targets:', error);
            }
        };

        fetchTargets();
    }, []);

    useEffect(() => {
        if (mode === 'Spec') {
            const fetchSpecOptions = async () => {
                try {
                    const response = await axios.get('http://127.0.0.1:5000/api/spec-options');
                    setSpecFileOptions(response.data);
                } catch (error) {
                    console.error('Error fetching spec file options:', error);
                }
            };

            fetchSpecOptions();
        }
    }, [mode]);

    useEffect(() => {
        if (selectedSpecFile) {
            const fetchSpecFileData = async () => {
                try {
                    const response = await axios.get(
                        `http://127.0.0.1:5000/api/spec-file?file=${selectedSpecFile}`
                    );
                    setWavelengths(response.data.wavelengths);
                    setFilters(response.data.filters);
                } catch (error) {
                    console.error('Error fetching spec file data:', error);
                }
            };

            fetchSpecFileData();
        }
    }, [selectedSpecFile]);

    const handleInputChange = (event, value) => {
        setTarget(value);
        const selectedTarget = targets.find((t) => t.name === value);
        if (selectedTarget) {
            setRa(selectedTarget.ra);
            setDec(selectedTarget.dec);
            setIsCustomTarget(false);
        } else {
            setRa('');
            setDec('');
            setIsCustomTarget(true);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://127.0.0.1:5000/api/send_email', {
                target,
                ra,
                dec,
                exposure,
                mode,
            });
            setMessage(response.data.message);
        } catch (error) {
            console.error('Error sending email:', error);
            setMessage('Failed to submit form.');
        }
    };

    return (
        <div className="container">
            <form onSubmit={handleSubmit} className="form">
                <div className="group-container">
                    <label className="default-label">Target:</label>
                    <Autocomplete
                        options={targets}
                        getOptionLabel={(option) => option.name || ''}
                        inputValue={target}
                        onInputChange={handleInputChange}
                        renderInput={(params) => (
                            <TextField {...params} label="Target" variant="outlined" fullWidth size="small" />
                        )}
                        freeSolo
                        className="input-field"
                    />
                </div>
                {/* RA, Dec, and other fields */}
                {mode === 'Spec' && (
                    <div className="spec-section">
                        <div className="group-container">
                            <label className="default-label">Spec File:</label>
                            <Select
                                value={selectedSpecFile}
                                onChange={(e) => setSelectedSpecFile(e.target.value)}
                                fullWidth
                                size="small"
                            >
                                {specFileOptions.map((file) => (
                                    <MenuItem key={file} value={file}>
                                        {file}
                                    </MenuItem>
                                ))}
                            </Select>
                        </div>
                        <div className="wavelength-container">
                            <canvas ref={chartRef} />
                        </div>
                        <div className="filters-container">
                            {['u', 'g', 'r', 'i', 'z'].map((filter) => (
                                <span
                                    key={filter}
                                    className={`filter ${
                                        filters.includes(filter) ? 'filter-active' : 'filter-inactive'
                                    }`}
                                >
                                    {filter}
                                </span>
                            ))}
                        </div>
                    </div>
                )}
                <Button type="submit" variant="contained" color="primary" className="submit-button">
                    Submit
                </Button>
            </form>
            {message && <p className="message">{message}</p>}
        </div>
    );
}

export default TargetForm;
