import React, { useState, useEffect } from 'react';
import axios from 'axios';

function MultitelescopesTable() {
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(true);

    // Fetch multitelescopes data
    useEffect(() => {
        axios.get('http://127.0.0.1:5000/api/multitelescopes')
            .then(response => {
                setData(response.data);
                setLoading(false);
            })
            .catch(error => {
                console.error("Error fetching multitelescopes data:", error);
                setLoading(false);
            });
    }, []);

    // Status Indicator: Color-coding logic
    const getStatusColor = (status) => {
        if (status === "operational") return "green";
        if (status === "maintanance") return "yellow";
        if (status === "offline") return "red";
        return "gray";
    };

    if (loading) {
        return <p>Loading data...</p>;
    }

    return (
        <div className="container">
            <h2>Multitelescopes Status</h2>
            <table border="1" cellPadding="5" cellSpacing="0" style={{ width: '100%' }}>
                <thead>
                    <tr>
                        <th>Telescope</th>
                        <th>Component</th>
                        <th>Status</th>
                        <th>Active</th>
                        <th>Timestamp</th>
                        <th>Reported By</th>
                    </tr>
                </thead>
                <tbody>
                    {data.map((row, index) => (
                        <tr key={index}>
                            <td>{row.Telescope}</td>
                            <td>{row.Component}</td>
                            <td>
                                <span
                                    style={{
                                        display: "inline-block",
                                        width: "10px",
                                        height: "10px",
                                        backgroundColor: getStatusColor(row.Status),
                                        borderRadius: "50%",
                                        marginRight: "8px"
                                    }}
                                ></span>
                                {row.Status}
                            </td>
                            <td>{row.IsActive ? "Yes" : "No"}</td>
                            <td>{row.Timestamp}</td>
                            <td>{row.ReportedBy}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default MultitelescopesTable;
