import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/TargetForm.css';
import { Autocomplete, TextField, Button, RadioGroup, FormControlLabel, Radio, MenuItem, Select } from '@mui/material';

import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip } from 'chart.js';
import { Bar } from 'react-chartjs-2';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip);

function TargetForm() {
    const [targets, setTargets] = useState([]);
    const [target, setTarget] = useState('');
    const [ra, setRa] = useState('');
    const [dec, setDec] = useState('');
    const [exposure, setExposure] = useState('');
    const [mode, setMode] = useState('Spec'); // Default value is 'Spec'
    const [specFileOptions, setSpecFileOptions] = useState(['specall.specmode']); // State for file options
    const [selectedSpecFile, setSelectedSpecFile] = useState('specall.specmode'); // Selected file
    const [message, setMessage] = useState('');
    const [isCustomTarget, setIsCustomTarget] = useState(true); // Allow RA/DEC editing by default
    const [specFileData, setSpecFileData] = useState({});

    const wavelengthRange = Array.from({ length: 61 }, (_, i) => 3000 + i * 100); // 3000Å to 9000Å in 100Å steps
    const filters = ['u', 'r', 'g', 'i', 'z']; // Updated filters

    useEffect(() => {
        // Fetch targets from the backend
        axios.get('http://127.0.0.1:5000/api/targets')
            .then(response => {
                setTargets(response.data);
            })
            .catch(error => {
                console.error('Error fetching targets:', error);
            });
    }, []);


    useEffect(() => {
        if (mode === 'Spec') {
            // Fetch file options from the backend
            axios.get('http://127.0.0.1:5000/api/spec-options')
                .then(response => {
                    setSpecFileOptions(response.data); // Update state with fetched filenames
                })
                .catch(error => {
                    console.error('Error fetching spec file options:', error);
                });
        }
    }, [mode]); // Fetch options when mode changes to "Spec"

    useEffect(() => {
        // Fetch the spec file data
        axios.get('http://127.0.0.1:5000/api/spec-file?file=specall.specmode')
            .then((response) => {
                const spec = response.data;

                // Extract unique values from the spec file
                const uniqueList = Array.from(
                    new Set(Object.values(spec).flat())
                );

                // Separate wavelengths and filters
                const extractedWavelengths = uniqueList
                    .filter((item) => item.startsWith('m'))
                    .map((item) => parseFloat(item.slice(1)));
                extractedWavelengths.sort((a, b) => a - b);

                const extractedFilters = uniqueList.filter(
                    (item) => !item.startsWith('m')
                );

                setWavelengths(extractedWavelengths);
                setFilters(extractedFilters);
            })
            .catch((error) => console.error('Error fetching spec file data:', error));
    }, []);

    // Convert wavelength data to Chart.js-compatible format
    const getWavelengthDatasets = () => {
        const datasets = [];
        Object.keys(specFileData).forEach((key) => {
            specFileData[key].forEach((value) => {
                if (value.startsWith('m')) {
                    const wavelength = parseInt(value.slice(1)) * 10; // Convert to Å
                    datasets.push({
                        label: `${key} - ${value}`,
                        backgroundColor: 'green',
                        borderColor: 'black',
                        borderWidth: 1,
                        data: [{ x: wavelength - 125, y: key }, { x: wavelength + 125, y: key }],
                    });
                }
            });
        });
        return datasets;
    };

    const handleInputChange = (event, value) => {
        setTarget(value);

        // Check if the input matches a predefined target
        const selectedTarget = targets.find((t) => t.name === value);
        if (selectedTarget) {
            // If a predefined target is selected
            setRa(selectedTarget.ra);
            setDec(selectedTarget.dec);
            setIsCustomTarget(false); // Disable RA/DEC editing
        } else {
            // If it's a custom target
            setRa('');
            setDec('');
            setIsCustomTarget(true); // Enable RA/DEC editing
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://127.0.0.1:5000/api/send_email', {
                target,
                ra,
                dec,
                exposure,
                mode,
            });
            setMessage(response.data.message);
        } catch (error) {
            console.error('Error sending email:', error);
            setMessage('Failed to submit form.');
        }
    };

    const getFilterDatasets = () => {
        const datasets = filters.map((filter) => ({
            label: filter,
            backgroundColor: Object.values(specFileData) // Convert object values to an array
                .flat() // Flatten the nested arrays
                .includes(filter) // Check if the filter exists
                ? 'blue'
                : 'transparent',
            data: [filter], // Add the filter as data
        }));
        return datasets;
    };

    const data = {
        labels: ['Wavelengths'], // Placeholder label for the x-axis
        datasets: wavelengths.map((wav, index) => ({
            label: `Wavelength ${wav}nm`,
            data: [(wav + 12.5) * 10 - (wav - 12.5) * 10], // Span width
            backgroundColor: 'rgba(0, 128, 0, 0.6)', // Green color
            borderColor: 'black',
            borderWidth: 1,
            barThickness: 20,
            xAxisID: 'x',
        })),
    };


    const options = {
        responsive: true,
        plugins: {
            tooltip: {
                enabled: false, // Disable tooltips
            },
            title: {
                display: true,
                text: 'Wavelength Visualization',
            },
        },
        scales: {
            x: {
                type: 'linear',
                title: {
                    display: true,
                    text: 'Wavelength (Å)',
                },
                min: Math.min(...wavelengths) * 10 - 1250, // Adjust range based on wavelengths
                max: Math.max(...wavelengths) * 10 + 1250,
            },
            y: {
                display: false, // Hide the y-axis
            },
        },
    };

    return (
        <div>
            <h2>7DT Target of Opportunity (ToO) Request</h2>
            <form onSubmit={handleSubmit} className="form">
                <div className="group-container">
                    <label className="default-label">Target:</label>
                    <Autocomplete
                        options={targets}
                        getOptionLabel={(option) => option.name || ''}
                        inputValue={target}
                        onInputChange={handleInputChange} // Triggered as user types
                        renderInput={(params) => (
                            <TextField
                                {...params}
                                label="Target"
                                variant="outlined"
                                fullWidth
                            />
                        )}
                        freeSolo // Allows custom input
                        openOnFocus // Opens dropdown when focused
                        className="input-field"
                    />
                </div>
                <div className="group-container">
                    <label className="default-label">R.A.:</label>
                    <TextField
                        label="hh:mm:ss or degrees"
                        variant="outlined"
                        value={ra}
                        onChange={(e) => setRa(e.target.value)}
                        fullWidth
                        disabled={!isCustomTarget} 
                        className="input-field"
                    />
                </div>
                <div className="group-container">
                    <label className="default-label">Dec.:</label>
                    <TextField
                        label="dd:mm:ss or degrees"
                        variant="outlined"
                        value={dec}
                        onChange={(e) => setDec(e.target.value)}
                        fullWidth
                        disabled={!isCustomTarget} 
                        className="input-field"
                    />
                </div>
                <div className="group-container">
                    <label className="default-label">Exposure:</label>
                    <TextField
                        label="seconds"
                        variant="outlined"
                        value={exposure}
                        onChange={(e) => setExposure(e.target.value)}
                        fullWidth
                        className="input-field"
                    />
                </div>
                <div className="group-container">
                    <label className="default-label">Mode:</label>
                    <RadioGroup
                        row
                        value={mode}
                        onChange={(e) => setMode(e.target.value)}
                    >
                        <FormControlLabel value="Spec" control={<Radio />} label="Spec" />
                        <FormControlLabel value="Deep" control={<Radio />} label="Deep" />
                    </RadioGroup>
                </div>
                {mode === 'Spec' && (
                    <div>
                        <div className="group-container">
                            <label className="default-label">Spec File:</label>
                            <Select
                                value={selectedSpecFile}
                                onChange={(e) => setSelectedSpecFile(e.target.value)}
                                fullWidth
                            >
                                {specFileOptions.map((file) => (
                                    <MenuItem key={file} value={file}>
                                        {file}
                                    </MenuItem>
                                ))}
                            </Select>
                        </div>
                        <div>
                            <h2>Wavelength Visualization</h2>
                            <div>
                                <label>Select Spec File:</label>
                                <select
                                    value={selectedSpecFile}
                                    onChange={(e) => setSelectedSpecFile(e.target.value)}
                                >
                                    {specFileOptions.map((file) => (
                                        <option key={file} value={file}>
                                            {file}
                                        </option>
                                    ))}
                                </select>
                            </div>
                            {selectedSpecFile && <Bar data={data} options={options} />}
                        </div>
                    </div>
                )}
                <Button
                    type="submit"
                    variant="contained"
                    color="primary"
                    fullWidth
                    className="submit-button"
                >
                    Submit
                </Button>
            </form>
            {message && <p className="message">{message}</p>}
        </div>
    );
}

export default TargetForm;
