import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/StatusTable.css';

function FiltInfoTable() {
    const [data, setData] = useState([]);
    const [loading, setLoading] = useState(true);

    // Fetch filtinfo data
    useEffect(() => {
        axios.get('http://127.0.0.1:5000/api/filtinfo')
            .then(response => {
                setData(response.data);
                setLoading(false);
            })
            .catch(error => {
                console.error("Error fetching filtinfo data:", error);
                setLoading(false);
            });
    }, []);

    if (loading) {
        return <p>Loading data...</p>;
    }

    return (
        <div className="container">
            <h2>Available Filter List</h2>
            <table border="1" cellPadding="5" cellSpacing="0" style={{ width: '100%' }}>
                <thead>
                    <tr>
                        <th>Telescope</th>
                        <th>Filters</th>
                    </tr>
                </thead>
                <tbody>
                    {data.map((row, index) => (
                        <tr key={index}>
                            <td>{row.Telescope}</td>
                            <td>{row.Filters.join(', ')}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default FiltInfoTable;
