import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/StatusTable.css';

function StatusTable() {
    const [tableData, setTableData] = useState([]);
    const [latestReport, setLatestReport] = useState({});
    const [loading, setLoading] = useState(true);

    // Fetch status data
    useEffect(() => {
        axios.get('http://127.0.0.1:5000/api/status')
            .then(response => {
                setTableData(response.data.table);
                setLatestReport(response.data.latest_report);
                setLoading(false);
            })
            .catch(error => {
                console.error("Error fetching status data:", error);
                setLoading(false);
            });
    }, []);

    // Status Indicator: Color-coding logic
    const getStatusColor = (status) => {
        if (status === "operational") return "green";
        if (status === "maintanance") return "yellow";
        if (status === "offline") return "red";
        return "gray";
    };

    if (loading) {
        return <p>Loading data...</p>;
    }

    return (
        <div className="container">
            <h2>Multitelescopes Status</h2>
            <table border="1" cellPadding="5" cellSpacing="0" style={{ width: '100%' }}>
                <thead>
                    <tr>
                        <th>Telescope</th>
                        <th>Mount</th>
                        <th>Focuser</th>
                        <th>Filterwheel</th>
                        <th>Camera</th>
                    </tr>
                </thead>
                <tbody>
                    {tableData.map((row, index) => (
                        <tr key={index}>
                            <td>{row.Telescope}</td>
                            <td style={{ backgroundColor: getStatusColor(row.Mount) }}></td>
                            <td style={{ backgroundColor: getStatusColor(row.Focuser) }}></td>
                            <td style={{ backgroundColor: getStatusColor(row.Filterwheel) }}></td>
                            <td style={{ backgroundColor: getStatusColor(row.Camera) }}></td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <p style={{ marginTop: "20px", fontStyle: "italic", textAlign: "right" }}>
                Reported by {latestReport.reported_by} on {latestReport.timestamp}
            </p>
        </div>
    );
}

export default StatusTable;
